package org.openecomp.portalsdk.core.restful.domain;

import com.fasterxml.jackson.annotation.JsonIgnoreProperties;

/**
 * This bean holds the information for a role in the role and user management
 * REST API.
 */

@JsonIgnoreProperties(ignoreUnknown = true)
public class EcompRole implements Comparable<EcompRole>{

	protected Long id;
	private String name;

	public Long getId() {
		return id;
	}

	public void setId(Long id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}	

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((id == null) ? 0 : id.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		EcompRole other = (EcompRole) obj;
		if (id == null) {
			if (other.id != null)
				return false;
		} else if (!id.equals(other.id))
			return false;
		return true;
	}

	@Override
	public String toString() {
		String s = "@EcompRole[id: " + id + "; name: " + name + "]";
		return s;
	}

	@Override
	public int compareTo(EcompRole o) {
		return this.id.compareTo(o.id);
	}

}
